#define rep(i, n) for(int i = 0; i < (n); ++i)

using namespace std;

typedef vector<int> V;
typedef vector<V> VV;

const int inf = 2e8;

int n, m, s;
int d[14348907];

V seq(){
	V v(n);
	rep(i, n){
		v[i] = i;
	}
	return v;
}

int pack(const VV& v){
	V p(n);
	rep(i, 3){
		for(int x: v[i]){
			p[x] = i;
		}
	}

	int k = 0;
	for(int i = n - 1; i >= 0; --i){
		k *= 3;
		k += p[i];
	}
	return k;
}
VV unpack(int k){
	VV v(3, V());
	rep(i, n){
		v[k % 3].push_back(i);
		k /= 3;
	}
	return v;
}

VV read(){
	VV v(3, V());
	rep(i, 3){
		int k;
		cin >> k;
		rep(j, k){
			int x;
			cin >> x;
			v[i].push_back(x - 1);
		}
	}
	return v;
}

VV swap_cup(VV v, int a, int b){
	if(!v[a].empty() || !v[b].empty()){
		if(!v[a].empty() && !v[b].empty()){
			if(v[a].back() > v[b].back()){
				swap(a, b);
			}	
		}
		else if(v[b].empty()){
			swap(a, b);
		}
		v[a].push_back(v[b].back());
		v[b].pop_back();
	}
	return v;
}

int main(){
	while(1){
		cin >> n >> m;
		if(!n){
			break;
		}
		s = pack(read());

		fill_n(d, sizeof(d) / sizeof(d[0]), inf);

		queue<int> q;
		d[s] = 0;
		q.push(s);
		while(!q.empty()){
			int v = q.front();
			q.pop();
			VV k = unpack(v);
			rep(i, 2){
				int t = pack(swap_cup(k, 1, i * 2));
				if(d[t] == inf){
					d[t] = d[v] + 1;
					q.push(t);
				}
			}
		}

		int ans = min(d[pack({seq(), {}, {}})], d[pack({{}, {}, seq()})]);
		cout << (ans <= m ? ans : -1) << endl;
	}
	return 0;
}

#include <iostream>
#include <stack>

typedef std::stack<int> Tray;

bool moveNtoN(Tray tr[], int src, int dst){
    if((tr[src]).empty() ||
        !(tr[dst]).empty() && (tr[src]).top() < (tr[dst]).top()){
        return false;
    }
    (tr[dst]).push((tr[src]).top());
    (tr[src]).pop();
    return true;
}

bool ifSorted(Tray tr[]){
    return ((tr[1]).empty() && ((tr[0]).empty() || (tr[2].empty())));
}

int solveCenter(Tray tr[], int m, int lastMove){
    for(int i = 1; i <= m; i++){
        if(lastMove != 1 && moveNtoN(tr, 0, 1)){
            lastMove = 0;
        }
        else if(lastMove != 0  && moveNtoN(tr, 1, 0)){
            lastMove = 1;
        }
        else if(lastMove != 3 && moveNtoN(tr, 1, 2)){
            lastMove = 2;
        }
        else if(lastMove != 2 && moveNtoN(tr, 2, 1)){
            lastMove = 3;
        }
        if(ifSorted(tr)){
            return i;
        }
    }
}

int solveLeft(Tray tr[], int m){
    if(moveNtoN(tr, 0, 1)){
        if(ifSorted(tr)){
            return 1;
        }
        int ret = solveCenter(tr, m - 1, 0) + 1;
        return ret ? ret : -1;
    }
    else if(moveNtoN(tr, 1, 0)){
        if(ifSorted(tr)){
            return 1;
        }
        int ret = solveCenter(tr, m - 1, 1) + 1;
        return ret ? ret : -1;
    }
}

int solveRight(Tray tr[], int m){
    if(moveNtoN(tr, 1, 2)){
        if(ifSorted(tr)){
            return 1;
        }
        int ret = solveCenter(tr, m - 1, 2) + 1;
        return ret ? ret : -1;
    }
    else if(moveNtoN(tr, 2, 1)){
        if(ifSorted(tr)){
            return 1;
        }
        int ret = solveCenter(tr, m - 1, 3) + 1;
        return ret ? ret : -1;
    }
}

int solve(Tray argTr[], int m){
    if(ifSorted(argTr)){
        return 0;
    }
    Tray tr[3];
    tr[0] = argTr[0];
    tr[1] = argTr[1];
    tr[2] = argTr[2];
    int ansl = solveLeft(tr, m);
    tr[0] = argTr[0];
    tr[1] = argTr[1];
    tr[2] = argTr[2];
    int ansr = solveRight(tr, m);
    if(ansl == -1 && ansr == -1){
        return -1;
    }
    else if(ansl == -1 || ansr == -1){
        return ansl > 0 ? ansl : ansr;
    }
    else{
        return ansl < ansr ? ansl : ansr;
    }
}

int main(){
    int n, m;
    while(1){
        std::cin >> n >> m;
        if(!(n || m)){
            break;
        }
        Tray trays[3];
        for(int col = 0; col < 3; col++){
            int nCups;                     // #cups on the line
            std::cin >> nCups;
            while(nCups--){
                int cup;
                std::cin >> cup;
                (trays[col]).push(cup);
            }
        }
        std::cout << solve(trays, m) << std::endl;
    }
    return 0;
}

#include <iostream>
