int solve(int a, int b, int c){
  if(a == 0 && b == 0) return 0;
  if(c & 1) return solve(a >> 1, b >> 1, c >> 1);
  if(b & 1) return solve(c >> 1, b >> 1, a >> 1) + 1 + solve((a|b|c) >> 1, 0, 0);
  if(a & 1) return solve(a >> 1, b >> 1, c >> 1) + 2*solve((a|b|c) >> 1, 0, 0) + 2;
}

