const int MAX = 14348907; // 3 ^ 15

int N,M,init;
bool memo[MAX];
vector<int> v[3];

struct state{
  int n,bit;
  state(int n=0, int bit=init):n(n),bit(bit){}
};

int calcBit(int n, int pos){
  return (int)pow(3,(double)pos) * n;
}

int calcN(int bit, int pos){
  return (bit / (int)pow(3,(double)pos)) % 3;
}

void add(int &bit, int pos, int n){
  bit -= calcBit(calcN(bit,pos), pos);
  bit += calcBit(n, pos);
}

void bfs(){
  queue<state> Q;
  state u,v;
  int max[3];
  fill(memo,memo+MAX,false);
  memo[init] = true;
  for(Q.push(u); !Q.empty();){
    u = Q.front();
    Q.pop();

    max[0] = max[1] = max[2] = -1;
    for(int i=N-1;i>=0;i--){
      if(max[calcN(u.bit,i)] == -1) max[calcN(u.bit,i)] = i;
    }

    if(max[1] == -1 && (max[0] == -1 || max[2] == -1)){
      //for(int i=0;i<N;i++) cout << calcN(u.bit,i) << ' ';
      cout << u.n << endl;
      return;
    }
    if(u.n >= M) break;

    if(max[0] > max[1]){
      v = u;
      add(v.bit, max[0], 1);
      v.n++;
      if(!memo[v.bit] ){
	memo[v.bit] = true;
	Q.push(v);
      }
    }
    if(max[1] > max[0]){
      v = u;
      add(v.bit, max[1], 0);
      v.n++;
      if(!memo[v.bit]){
	memo[v.bit] = true;
	Q.push(v);
      }
    }
    if(max[2] > max[1]){
      v = u;
      add(v.bit, max[2], 1);
      v.n++;
      if(!memo[v.bit]){
	memo[v.bit] = true;
	Q.push(v);
      }
    }
    if(max[1] > max[2]){
      v = u;
      add(v.bit, max[1], 2);
      v.n++;
      if(!memo[v.bit]){
	memo[v.bit] = true;
	Q.push(v);
      }
    }
  }
  cout << -1 << endl;
}


