ll encode(const vector<vector<int>>& cups) {
	ll res = 0;
	for (int i = 0; i < 3; i++) {
		int offset = 16 * i;

		for (int j = 0; j < cups[i].size(); j++) {
			int k = cups[i][j];
			res |= (1LL << (offset + k));
		}
	}
	return res;
}

set<ll> states;

void _add_next_state(queue<vector<vector<int>>>& qu, vector<vector<int>>& cups, int i, int j) {
	// i to j
	if (!cups[i].empty()) {
		if (cups[j].empty() || cups[j].back() < cups[i].back()) {
			cups[j].push_back(cups[i].back());
			cups[i].pop_back();
			ll s = encode(cups);
			if (states.find(s) == states.end()) {
				qu.push(cups);
				states.insert(s);
			}
			cups[i].push_back(cups[j].back());
			cups[j].pop_back();
		}
	}
}

