	//a to b
	_add_next_state(qu, cups, 0, 1);
	//b to a
	_add_next_state(qu, cups, 1, 0);
	//c to b
	_add_next_state(qu, cups, 2, 1);
	//b to c
	_add_next_state(qu, cups, 1, 2);
}

