int back( int mask )
{
  for( int i = n-1; i >= 0; --i ) if( mask >> i & 1 )
    return i;

  return -1;
}

void quePush( std::queue<State> &que, int cup[], int dist, int prv )
{
  int x = back( cup[0] ), y = back( cup[1] ), z = back( cup[2] );

  //printf( "(x,y,z)=(%d,%d,%d)\n", x, y, z );

  if( prv )
  {
    if( x > y )
      que.push( { { cup[0]^(1<<x), cup[1]|(1<<x), cup[2] }, dist+1, 0 } );
    else
      que.push( { { cup[0]|(1<<y), cup[1]^(1<<y), cup[2] }, dist+1, 0 } );
  }

  if( prv != 1 )
  {
    if( y > z )
      que.push( { { cup[0], cup[1]^(1<<y), cup[2]|(1<<y) }, dist+1, 1 } );
    else
      que.push( { { cup[0], cup[1]|(1<<z), cup[2]^(1<<z) }, dist+1, 1 } );
  }

  return;
}

