int cost[14348907]; // 3^15

int get_state(array<int, 3>& t) {
  int state = 0;
  REP(i, 15) {
    REP(j, 3) {
      if (t[j]&(1<<i)) {
        state += pow(3,i)*j;
      }
    }
  }
  return state;
}

int get_cost(array<int, 3>& t) {
  return cost[get_state(t)];
}

void set_cost(array<int, 3> t, int c) {
  cost[get_state(t)] = c;
}

int get_highest_bit(int a) {
  int ret = 0;
  REP(i, 15) {
    ret = (a & (1<<i)) ? a & (1<<i) : ret;
  }
  return ret;
}

//BFS
int bfs(array<int, 3> t, int n){
  fill_n(cost, sizeof(cost)/sizeof(int), NIL);
  set_cost(t, 0);

  deque<array<int, 3>> q;
  q.push_back(t);
  int goal = (1<<n) - 1;

  while(!q.empty()) {

    auto now = q.front(); q.pop_front();
    if (now[0] == goal || now[2] == goal) {
      return get_cost(now);
    }

    vector<pair<int, int>> c; // q
    //A->B
    if(now[0]>now[1]) {
      c.push_back(make_pair(0,1));
    }
    // B->C
    if(now[1]>now[2]) {
      c.push_back(make_pair(1,2));
    }
    // C->B
    if(now[2]>now[1]) {
      c.push_back(make_pair(2,1));
    }
    // B->A
    if(now[1]>now[0]) {
      c.push_back(make_pair(1,0));
    }

    for(pair<int,int>& p: c) {
      auto next = now;
      next[p.fi] &= ~get_highest_bit(now[p.fi]);
      next[p.se] |= get_highest_bit(now[p.fi]);

      if(get_cost(next) == NIL) {
        set_cost(next, get_cost(now)+1);
        q.push_back(next);
      }
    }
  }
  return NIL;
}

