    int ret = 0;
    while (1) {
        for (int i=0; i<=1; ++i) {
            if (tray[0].size() == n+1 || tray[2].size() == n+1) return ret;
            int a = tray[i].top(); tray[i].pop();
            int b = tray[i+1].top(); tray[i+1].pop();
            if (a < b) {
                tray[i].push(a);
                tray[i].push(b);
            } else {
                tray[i+1].push(b);
                tray[i+1].push(a);
            }
            ret ++;
        }
        if (m < ret) return 15000001;
    }
}

int main() {
    while (cin >> n >> m) {
        if (!n && !m) break;
        vector<stack<int>> tray(3);
        for (int i=0; i<3; ++i) {
            cin >> k;
            tray[i].push(-1);
            for (int j=0; j<k; ++j) {
                cin >> x;
                tray[i].push(x);
            }
        }
        int ans = chk(tray);
        swap(tray[0], tray[2]);
        ans = min(ans, chk(tray));
        cout << (ans <= m ? ans : -1) << endl;
    }
    return 0;
}

#include <bits/stdc++.h>

using namespace std;

int pow3[16];
int n, m;

void calc_pow3()
{
    pow3[0] = 1;

    for (int i = 1; i <= 15; ++i) {
        pow3[i] = pow3[i - 1] * 3;
    }
}

int solve(int n, int m)
{
    int c[16];
    for (int i = 0; i < 3; ++i) {
        int j;

        cin >> j;

        int temp;
        for (int k = 0; k < j; ++k) {
            cin >> temp;

            c[n - temp] = i;
        }
    }

    int ans = 0;
    int d;
    int p = 0;

    for (int i = n - 1; i >= 0; --i) {
        d = abs(c[i] - p);

        ans += pow3[i] * d;

        if (d == 1) p = 2 - p;


    }

    int ans2;

    ans2 = pow3[n] - 1 - ans;

    ans = min(ans, ans2);

    ans = (ans > m) ? -1: ans;

    return ans;
}

int main()
{
    calc_pow3();

    int n, m;

    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;

        cout << solve(n, m) << endl;
    }

    return 0;
}

