#include <iostream>
#include <algorithm>
#include <stack>
using namespace std;

enum class MovePos
{
	MoveLeftToCenter,
	MoveRightToCenter,
	MoveCenterToLeft,
	MoveCenterToRight
};

class Tray
{
public:
	stack<int> t[3];
	inline bool isCompleted()
	{
		if (t[0].empty() && t[1].empty()) return true;
		if (t[1].empty() && t[2].empty()) return true;
		return false;
	}
	inline bool canMoveLeftToCenter()
	{
		if (t[0].empty()) return false;
		if (t[1].empty()) return true;
		if (t[0].top() > t[1].top()) return true;
		return false;
	}
	inline bool canMoveRightToCenter()
	{
		if (t[2].empty()) return false;
		if (t[1].empty()) return true;
		if (t[2].top() > t[1].top()) return true;
		return false;
	}
	inline bool canMoveCenterToLeft()
	{
		if (t[1].empty()) return false;
		if (t[0].empty()) return true;
		if (t[1].top() > t[0].top()) return true;
		return false;
	}
	inline bool canMoveCenterToRight()
	{
		if (t[1].empty()) return false;
		if (t[2].empty()) return true;
		if (t[1].top() > t[2].top()) return true;
		return false;
	}
	inline void MoveLeftToCenter()
	{
		int top = t[0].top();
		t[0].pop();
		t[1].push(top);
	}
	inline void MoveRightToCenter()
	{
		int top = t[2].top();
		t[2].pop();
		t[1].push(top);
	}
	inline void MoveCenterToLeft()
	{
		int top = t[1].top();
		t[1].pop();
		t[0].push(top);
	}
	inline void MoveCenterToRight()
	{
		int top = t[1].top();
		t[1].pop();
		t[2].push(top);
	}
};

int calc(Tray t, MovePos previousMovePos, int m)
{
	for (int i = 1; i <= m; i++)
	{
		if (t.isCompleted()) return i;
		if (t.canMoveLeftToCenter() && previousMovePos != MovePos::MoveCenterToLeft)
		{
			t.MoveLeftToCenter();
			previousMovePos = MovePos::MoveLeftToCenter;
		}
		else if (t.canMoveRightToCenter() && previousMovePos != MovePos::MoveCenterToRight)
		{
			t.MoveRightToCenter();
			previousMovePos = MovePos::MoveRightToCenter;
		}
		else if (t.canMoveCenterToLeft() && previousMovePos != MovePos::MoveLeftToCenter)
		{
			t.MoveCenterToLeft();
			previousMovePos = MovePos::MoveCenterToLeft;
		}
		else if (t.canMoveCenterToRight() && previousMovePos != MovePos::MoveRightToCenter)
		{
			t.MoveCenterToRight();
			previousMovePos = MovePos::MoveCenterToRight;
		}
		if (i > 1251598) return 99999999;
	}
	return 99999999;
}

int main()
{
	int n, m;
	while (cin >> n >> m, n + m)
	{
		Tray tray;

		for (int i = 0; i < 3; i++)
		{
			int a, b;
			cin >> a;
			for (int j = 0; j < a; j++)
			{
				cin >> b;
				tray.t[i].push(b);
			}
		}

		int ans = 99999999;
		if (tray.canMoveCenterToLeft())
		{
			Tray t = tray;
			t.MoveCenterToLeft();
			ans = min(ans, calc(t, MovePos::MoveCenterToLeft, m));
		}
		if (tray.canMoveCenterToRight())
		{
			Tray t = tray;
			t.MoveCenterToRight();
			ans = min(ans, calc(t, MovePos::MoveCenterToRight, m));
		}
		if (tray.canMoveLeftToCenter())
		{
			Tray t = tray;
			t.MoveLeftToCenter();
			ans = min(ans, calc(t, MovePos::MoveLeftToCenter, m));
		}
		if (tray.canMoveRightToCenter())
		{
			Tray t = tray;
			t.MoveRightToCenter();
			ans = min(ans, calc(t, MovePos::MoveRightToCenter, m));
		}
		if (ans == 99999999) cout << -1 << endl;
		else cout << ans << endl;
	}

	return 0;
}

#include<iostream>
