int f( int a, int b, int c )
{
  	if( !a && !b ) return 0;

	if( c & 1 ) return f( a >> 1, b >> 1, c >> 1 );
	if( b & 1 ) return f( c >> 1, b >> 1, a >> 1 ) + 1 + f( ( a | b | c ) >> 1, 0, 0 );
	if( a & 1 ) return f( a >> 1, b >> 1, c >> 1 ) + 2 + 2 * f( ( a | b | c ) >> 1, 0, 0 );
}       

