int n, m, cup[15];
int ipow(int t, int p)
{
	int res = 1;
	while (p--)
		res *= t;
	return res;
}
int count(bool left_base)
{
	int res = 0;
	for (int i = 0; i < n; ++i)
	{
		if (left_base && cup[i] == 2
			|| !left_base && cup[i] == 0)
			res += 2 * ipow(3, n-1 - i);
		else if (cup[i] == 1)
		{
			res += ipow(3, n-1 - i);
			left_base ^= true;
		}
	}
	return res;
}
int main()
{
	while (scanf("%d%d", &n, &m), n|m)
