int n,m;
struct state{
  vector<int> st[3];
  int co;
  state(){
    for(int i=0;i<3;i++){
      while(!st[i].empty()) st[i].pop_back();
    }
  }
  void print(){
    for(int i=0;i<3;i++){
      if(st[i].empty()){
	printf("[%d]...empty\n",i);	
	continue;
      }
      printf("[%d]...size=%d  back()=%d\n",i,(int)st[i].size(),st[i].back());
    }
    sleep(1);
  }
};
map<int,map<int,map<int,int> > > M;
bool check(state p){
  if(p.co>=1000000) return true;
    int a[3]={};
    for(int i=0;i<3;i++){
      for(int j=0;j<(int)p.st[i].size();j++){
	a[i] |= (1<<p.st[i][j]); 
      }
    }
    if(M[a[0]][a[1]][a[2]]!=0) return false;
    M[a[0]][a[1]][a[2]]=1;
    return true;
}
int solve(state s){
  queue<state> q;
  while(!q.empty()) q.pop();
  q.push(s);
  check(s);
  while(!q.empty()){
    state p = q.front(); q.pop();
    //    p.print();
    int c=p.co;
    if(c>m) break;
    if((int)p.st[0].size()==n || (int)p.st[2].size()==n) return c;
    p.co++;
    state tmp = p;
    if( !p.st[0].empty() && p.st[1].empty() ){
      goto change1;
    }else if( p.st[0].empty() && !p.st[1].empty()){
      goto change2;
    }
    if(!p.st[0].empty() && !p.st[1].empty()){
      if(p.st[0].back() > p.st[1].back()){
      change1:
	tmp.st[1].push_back(tmp.st[0].back());
	tmp.st[0].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      } else {
      change2:
	tmp.st[0].push_back(tmp.st[1].back());
	tmp.st[1].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      }
    }

    if( !p.st[1].empty() && p.st[2].empty() ){
      goto change3;
    }else if( p.st[1].empty() && !p.st[2].empty()){
      goto change4;
    }    
    if( !p.st[1].empty() && !p.st[2].empty() ){
      if(p.st[1].back() > p.st[2].back()){
      change3:
	tmp.st[2].push_back(tmp.st[1].back());
	tmp.st[1].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      } else {
      change4:
	tmp.st[1].push_back(tmp.st[2].back());
	tmp.st[2].pop_back();
	if(check(tmp))
	  q.push(tmp);
	tmp = p;
      }
    }

  }
  return -1;
}
main(){
  while(1){
    scanf("%d %d",&n,&m);
    if(!n && !m)break;
    state s;
    for(int i=0;i<3;i++){
      int p;
      scanf("%d",&p);
      for(int j=0;j<p;j++){
	int e;
	scanf("%d",&e);
	s.st[i].push_back(e);
      }
    }
  //  s.print();
    s.co=0;
    printf("%d\n",solve(s));
    M.clear();
  }
}

