#include <iostream>
#include <algorithm>
#include <set>
#include <map>

int nisehanoi(std::vector<std::set<int, std::greater<int>>>& tray, int n, int m, int result, int count = 0){
  if(count > std::min(m, result)) return result;
  if(tray[0].size() == n || tray[2].size() == n) return count;
  for(int i = 0; i < 3; ++i){
    for(int j = std::max(0, i - 1); j < std::min(3, i + 2); ++j){
      if(i != j && *tray[i].begin() > *tray[j].begin()){
        tray[j].insert(*tray[i].begin());
        tray[i].erase(*tray[i].begin());
        result = nisehanoi(tray, n, m, result, count + 1);
        tray[i].insert(*tray[j].begin());
        tray[j].erase(*tray[j].begin());
      }
    }
  }
  return result;
}
int main(){
  int n, m;
  while(std::cin >> n >> m, n + m > 0){
    std::vector<std::set<int, std::greater<int>>> tray;
