using namespace std;

inline int msb(int x) {
	if(!x) return 0;
	return (1 << (31 - __builtin_clz(x)));
}

struct state {
	vector<int> cup, prev;
	int cnt;

	state(const vector<int>& c, const vector<int>& p, const int& cnt_):cup(c), prev(p), cnt(cnt_) {}
};

int main() {
	const vector<int> init(3, -1);

	for(int n, m; scanf("%d %d", &n, &m), n | m;) {
		vector<int> cup(3, 0);

		for(int i = 0; i < 3; ++i) {
			int num;
			scanf("%d", &num);

			for(int j = 0; j < num; ++j) {
				int size;
				scanf("%d", &size);

				cup[i] |= (1 << (size - 1));
			}
		}

		int ans = INT_MAX;
		stack<state> sta;
		sta.push(state(cup, init, 0));
		while(!sta.empty()) {
			state s = sta.top();
			sta.pop();

			if(s.cnt > min(m, ans))
				continue;;

			if(s.cup[0] == (1 << n) - 1 || s.cup[2] == (1 << n) - 1) {
				ans = s.cnt;
				continue;
			}

			for(int i = 0; i < 2; ++i) {
				int move = max(msb(s.cup[i]), msb(s.cup[i + 1]));
				vector<int> next(s.cup);
				next[i] ^= move;
				next[i + 1] ^= move;
				if(next != s.prev)
					sta.push(state(next, s.cup, s.cnt + 1));
			}	
		}

		printf("%d\n", (ans == INT_MAX ? -1 : ans));
	}

	return EXIT_SUCCESS;
}

