#define MAX_N 15
#define MAX_M 15000000


std::stack<int> trays[3];
std::stack<int> r_trays[3];

void moveTop(int i, int j) {
  // move top value i to j
  trays[j].push(trays[i].top());
  trays[i].pop();
}

void clearTray() {
  for (int i = 0; i < 3; i ++) {
    while(!trays[i].empty())
      trays[i].pop();
    while(!r_trays[i].empty())
      r_trays[i].pop();
  }
}

bool isEmpty() {
  if((trays[0].empty() && trays[1].empty()) || (trays[1].empty() && trays[2].empty()))
    return true;
  else
    return false;
}

int moveCups(int m) {
  int move = 0;
  bool flag = true;

  if (isEmpty())
    return 0;

  while(flag) {
    for (int i = 0; i < 2; i ++) { 
      move ++;
      // move bigger cup
      if (trays[i + 1].empty() || (!trays[i].empty() && trays[i].top() > trays[i + 1].top())) {
        moveTop(i, i + 1); 
      } else {
        moveTop(i + 1, i);
      }
      // check situation
      if (isEmpty()) {
        flag = false;
        break;
      } else if (move >= m) {
        flag = false;
        move = -1;
        break;
      }
    }
  }
  return move;
}

int main() {
  int n, m;
  int num, temp, move, left, right;
  bool flag = true;
  while (1) {
    std::cin >> n >> m;
    if (n == 0)
      break;
    // store cups to trays.
    for (int i = 0; i < 3; i ++) {
      std::cin >> num;
      for (int j = 0; j < num; j ++) {
        std::cin >> temp;
        trays[i].push(temp);
      }
      r_trays[2 - i] = trays[i];
    }
    std::reverse(std::begin(r_trays), std::end(r_trays));
    right = moveCups(m);
    for (int i = 0; i < 3; i ++)
      trays[i].swap(r_trays[2 - i]);
    left = moveCups(m);

    std::cout << (right > left ? right : left) << std::endl;
    clearTray();
  }
  return 0;
}

