				int tmp; scanf("%d",&tmp);
				tray[k].push(tmp);
			}
		}

		if(over()){ puts("0"); continue; }

		int ans=(1<<31)-1;
		rep(i,4){
			stack<int> tmp[3];
			copy(tray,tray+3,tmp);
			if(move(i)) ans=min(ans,simulate(i)+1);
			copy(tmp,tmp+3,tray);
		}
		if(ans>m) ans=-1;
		printf("%d\n",ans);
	}

	return 0;
}

