struct State {
    array<vector<i64>,3> v;

    vector<State> neighbor() const {
        vector<State> res;

        if(movable(0,1)) {
            State st(*this);
            st.mv(0,1);
            res.emplace_back(st);
        }
        else if(movable(1,0)) {
            State st(*this);
            st.mv(1,0);
            res.emplace_back(st);
        }

        if(movable(1,2)) {
            State st(*this);
            st.mv(1,2);
            res.emplace_back(st);
        }
        else if(movable(2,1)) {
            State st(*this);
            st.mv(2,1);
            res.emplace_back(st);
        }

        return res;
    }

    void mv(i64 from, i64 to) {
        v[to].emplace_back(POP_BACK(v[from]));
    }

    bool movable(i64 from, i64 to) const {
        if(v[from].empty()) return false;
        if(v[to].empty()) return true;
        return v[to].back() < v[from].back();
    }

    bool done() const {
        return v[1].empty() && (v[0].empty() || v[2].empty());
    }
};

bool operator==(const State& lhs, const State& rhs) {
    return lhs.v == rhs.v;
}

template<>
struct myhash<State> {
    size_t operator()(const State& st) const {
        size_t seed = 0;
        for(const auto& e : st.v) {
            SEQHASH(seed, e);
        }
        return seed;
    }
};

i64 bfs(i64 M, State st_start) {
    HashSet<State> visited;
    queue<pair<State,i64>> que;

    visited.emplace(st_start);
    que.emplace(st_start, 0);

    while(!que.empty()) {
        State st; i64 d; tie(st,d) = POP(que);

        if(d > M) return -1;
        if(st.done()) return d;

        for(const auto& to : st.neighbor()) {
            if(visited.count(to)) continue;

            visited.emplace(to);
            que.emplace(to, d+1);
        }
    }

    assert(false);
}

void solve(i64 /*N*/, i64 M, State st) {
    i64 ans = bfs(M, st);
    PRINTLN(ans);
}

signed main(signed /*argc*/, char** /*argv*/) {
    while(true) {
        i64 N; RD(N);
        i64 M; RD(M);
        if(N == 0 && M == 0) break;

        State st;
        REP(i, 3) {
            i64 k; RD(k);
            RD(st.v[i], k);
        }

        solve(N, M, move(st));
    }

    EXIT();
}

