	int ta = a & ~(1<<(am-1));
	int tb = b | (1<<(am-1));
	if( hii.find(mp(ta,mp(tb,c)))==hii.end() ){
#if DEB
	  puts("a -> b");
	  printf("::::(%d,%d,%d)\n",ta,tb,c);
#endif
	  hii[mp(ta,mp(tb,c))] = step + 1;
	  q.push(mp(ta,mp(tb,c)));
	}
      }
      // b -> a
      if( bm>0 && am<bm ){
	int ta = a | (1<<(bm-1));
	int tb = b & ~(1<<(bm-1));
	if( hii.find(mp(ta,mp(tb,c)))==hii.end() ){
#if DEB
	  puts("b -> a");
	  printf("::::(%d,%d,%d)\n",ta,tb,c);
#endif
	  hii[mp(ta,mp(tb,c))] = step + 1;
	  q.push(mp(ta,mp(tb,c)));
	}
      }
      // b -> c
      if( bm>0 && bm>cm ){
	int tb = b & ~(1<<(bm-1));
	int tc = c | (1<<(bm-1));
	if( hii.find(mp(a,mp(tb,tc)))==hii.end() ){
#if DEB
	  puts("b -> c");
	  printf("::::(%d,%d,%d)\n",a,tb,tc);
#endif
	  hii[mp(a,mp(tb,tc))] = step + 1;
	  q.push(mp(a,mp(tb,tc)));
	}
      }
      // c -> b
      if( cm>0 && bm<cm ){
	int tb = b | (1<<(cm-1));
	int tc = c & ~(1<<(cm-1));
	if( hii.find(mp(a,mp(tb,tc)))==hii.end() ){
#if DEB
	  puts("c -> b");
	  printf("::::(%d,%d,%d)\n",a,tb,tc);
#endif
	  hii[mp(a,mp(tb,tc))] = step + 1;
	  q.push(mp(a,mp(tb,tc)));
	}
      }
