			if (i == 0 && prev != AB ){
				if (tray[1].empty() || cup > tray[1].top() ){
					tray[1].push (cup );
					tray[i].pop();
					ans = min (ans, dfs (curr+1, tray, AB ) );
					tray[i].push (cup );
					tray[1].pop();
				} // end if						
			}else
			if (i == 2 && prev != BC ){
				if (tray[1].empty() || cup > tray[1].top() ){
					tray[1].push (cup );
					tray[i].pop();
					ans = min (ans, dfs (curr+1, tray, BC ) );
					tray[i].push (cup );
					tray[1].pop();
				} // end if						
			}else
			if (i == 1 ){
				if (prev != AB && (tray[0].empty() || cup > tray[0].top() ) ){
					tray[0].push (cup );
					tray[i].pop();
					ans = min (ans, dfs (curr+1, tray, AB ) );
					tray[i].push (cup );
					tray[0].pop();
				}else
