					tray[2].push (cup );
					tray[i].pop();
					ans = min (ans, dfs (curr+1, tray, BC ) );
					tray[i].push (cup );
					tray[2].pop();
				} // end if
			} // end if
		} // en if
	} // end rep

	return ans;
}

