int bfs(int m,vector<int>& root, unordered_map<string,int> visited){

	priority_queue<P,vector<P>,greater<P> > que;
	que.push(P(0,root));

	vector<int> tmp;
	vector<int> sets;
	while(!que.empty()){
		int cost = que.top().first;

		sets = que.top().second;

		if(sets[1]==0 && sets[2]==0) return cost;
		if(sets[0]==0 && sets[1]==0) return cost;

		if(cost >= m) break;
		que.pop();
		for(int i=0;i+1<sets.size();i++){
			if((sets[i] && sets[i+1] && sets[i] < sets[i+1]) 
				|| (!sets[i] && sets[i+1])){
				tmp = sets;

				int pos=0;
				for(int j=15;j>=0;j--){
					if(tmp[i+1] & (1<<j)){
						pos = j;
						break;
					}
				}
				tmp[i+1] &= ~(1<<pos);
				tmp[i] |= (1<<pos);


				char buf[32];
				sprintf(buf,"%09d%09d%09d",tmp[0],tmp[1],tmp[2]);
				string next;
				next = buf;
				if(visited.find(next) == visited.end()){
					visited[next] = cost+1;
					que.push(P(cost+1,tmp));
				}
			}

			else if((sets[i] && sets[i+1] && sets[i] > sets[i+1]) 
				|| (sets[i] && !sets[i+1])){
				tmp = sets;

				int pos=0;
				for(int j=15;j>=0;j--){
					if(tmp[i] & (1<<j)){
						pos = j;
						break;
					}
				}
				tmp[i] &= ~(1<<pos);
				tmp[i+1] |= (1<<pos);

				char buf[32];
				sprintf(buf,"%09d%09d%09d",tmp[0],tmp[1],tmp[2]);
				string next;
				next = buf;
				if(visited.find(next) == visited.end()){
					visited[next] = cost+1;
					que.push(P(cost+1,tmp));
				}
			}
		}
	}
	return -1;
}

