inline bool finish() {
    return b.empty() && (a.empty() || c.empty());
}

// r: 
// prev: 
//  1: A->B
//  2: B->A
//  3: C->B
//  4: B->C
int dfs(int r, int prev) {
    if (finish()) return m-r;
    if (r == 0) return INF;
    int ret = INF;
    if (prev != 2 && !a.empty() && (b.empty() || a.back() > b.back())) {
        b.push_back(a.back()); a.pop_back();
        ret = min(ret, dfs(r-1, 1));
        a.push_back(b.back()); b.pop_back();
    }
    if (prev != 1 && !b.empty() && (a.empty() || b.back() > a.back())) {
        a.push_back(b.back()); b.pop_back();
        ret = min(ret, dfs(r-1, 2));
        b.push_back(a.back()); a.pop_back();
    } 
    if (prev != 4 && !c.empty() && (b.empty() || c.back() > b.back())) {
        b.push_back(c.back()); c.pop_back();
        ret = min(ret, dfs(r-1, 3));
        c.push_back(b.back()); b.pop_back();
    }
    if (prev != 3 && !b.empty() && (c.empty() || b.back() > c.back())) {
        c.push_back(b.back()); b.pop_back();
        ret = min(ret, dfs(r-1, 4));
        b.push_back(c.back()); c.pop_back();
    }
    return ret;
}

