using namespace std;

typedef unsigned int uint;
typedef long long ll;

int c[3];
int n;
int m;
int ans;
int depth;

enum{
  INIT = 0,
  AB = 1,
  BA = 2,
  BC = 3,
  CB = 4,
};

enum{
  A = 0,
  B = 1,
  C = 2
};

char prev[15000000+10];

static inline void dfs(){
  if(depth == m) return;
  if(depth >= ans) return;
  if(c[0] == (1 << n) - 1 || c[2] == (1 << n) - 1){
    ans = depth;
    return;
  }
  depth++;

  if(prev[depth-1] != BA && c[A] != 0){
    int aa = (c[A] & -c[A]);
    int bb = (c[B] & -c[B]);
    if(aa < bb || bb == 0){
      c[A] ^= aa;
      c[B] ^= aa;
      prev[depth] = AB;
      dfs();
      c[B] ^= aa;
      c[A] ^= aa;
    }
  }
  if(prev[depth-1] != AB && c[B] != 0){
    int bb = (c[B] & -c[B]);
    int aa = (c[A] & -c[A]);
    if(bb < aa || aa == 0){
      c[B] ^= bb;
      c[A] ^= bb;
      prev[depth] = BA;
      dfs();
      c[A] ^= bb;
      c[B] ^= bb;
    }
  }
  if(prev[depth-1] != CB && c[B] != 0){
    int bb = (c[B] & -c[B]);
    int cc = (c[C] & -c[C]);
    if(bb < cc || cc == 0){
      c[B] ^= bb;
      c[C] ^= bb;
      prev[depth] = BC;
      dfs();
      c[C] ^= bb;
      c[B] ^= bb;
    }
  }
  if(prev[depth-1] != BC && c[C] != 0){
    int cc = (c[C] & -c[C]);
    int bb = (c[B] & -c[B]);
    if(cc < bb || bb == 0){
      c[C] ^= cc;
      c[B] ^= cc;
      prev[depth] = CB;
      dfs();
      c[B] ^= cc;
      c[C] ^= cc;
    }
  }
  depth--;
}

int main(){
  while(scanf("%d%d",&n,&m),n+m){
    REP(i,3){
      int t;
      c[i] = 0;
      scanf("%d", &t);
      REP(j,t){
        int p;
        scanf("%d",&p);
        c[i] |= (1 << (n - p));
      }
    }

    ans = INT_MAX; depth = 0;
    prev[0] = INIT;
    dfs();
    printf("%d\n",ans == INT_MAX ? -1 : ans);
  }
  return 0;
}

#include <iostream>
