    template<typename T> bool operator<(const stack<T>& a, const stack<T>& b){
        if (a.size() != b.size()) return a.size()<b.size();
        for (auto ta = a, tb = b; !ta.empty(); ta.pop(), tb.pop()){
            auto &ea = a.top(), &eb = b.top();
            if (ea != eb) return ea < eb;
        }
        return false;
    }
}

struct state {
    stack<int> cup[3];
    unsigned d;
    stack<int>& operator[](int i){
        return cup[i];
    }
    bool able(int f, int t){
        return cup[f].size()>0 &&
            (cup[t].size() == 0 || cup[t].top()<cup[f].top());
    }
    void move(int f, int t){
        cup[t].push(cup[f].top());
        cup[f].pop();
    }
    bool operator<(const state &s)const{
        rep(i, 3){
            if (cup[0] != s.cup[0]) return cup[0]<s.cup[0];
            if (cup[1] != s.cup[1]) return cup[1]<s.cup[1];
            if (cup[2] != s.cup[2]) return cup[2]<s.cup[2];
        }
        return false;
    }
};

