ll calc(priority_queue<int> &q, int cnt, int pos) {
	//cout << n << "," << cnt << "," << pos << endl;
	if(cnt>=n) return 0;

	cnt++;

	int temp = q.top();q.pop();
	int q_num = (temp>>2)*-1;
	int q_pos = (temp&0b11);

	if(q_pos==pos) {
		return calc(q,cnt,pos);
	}
	if(q_pos==1) {
		return calc(q,cnt,pos^0b10)+1LL+matome[n-cnt-1];
	}else {
		ll rest = calc(q,cnt,pos);
		rest += 2LL + matome[n-cnt-1]*2LL;
		return rest;
	}
}

