                v[big[0]] = 1;
                int tmp = enc(v);
                if(dp[tmp] == INF){
                    dp[tmp] = dp[p]+1;
                    q.push(tmp);
                }
                v[big[0]] = 0;
            }
            if(big[1] > big[0]){
                v[big[1]] = 0;
                int tmp = enc(v);
                if(dp[tmp] == INF){
                    dp[tmp] = dp[p]+1;
                    q.push(tmp);
                }
                v[big[1]] = 1;
            }
            if(big[1] > big[2]){
                v[big[1]] = 2;
                int tmp = enc(v);
                if(dp[tmp] == INF){
                    dp[tmp] = dp[p]+1;
                    q.push(tmp);
                }
                v[big[1]] = 1;
