	if(b&1)
		return toC(c>>1,b>>1,a>>1)+toC((a|b|c)>>1,0,0)+1;
	if(a&1)
		return toC(a>>1,b>>1,c>>1)+2*toC((a|b|c)>>1,0,0)+2;
}

