// 
SI top(SI bits){
	for(int i=(1<<n) ; i ; i >>= 1 ){
		if( i & bits ) return i;
	}
	return 0;
}

// 
int bfs(){
	// d[s] := cup s 
	map<State,int> d;
	queue<State> q;

	State s = State( cup[0] , P(cup[1],cup[2]) );
	q.push(s);
	d[s] = 0;

	while( !q.empty() ){
		State now = q.front();
		int cnt = d[now];
		q.pop();

		// a,b,c := 
		// a_top,b_top,c_top := 
		SI& a = now.first;
		SI& b = now.second.first;
		SI& c = now.second.second;
		SI a_top = top(a);
		SI b_top = top(b);
		SI c_top = top(c);
		//debug(a,b,c,cnt);

		// 
		if( a == ((1<<n)-1) || c == ((1<<n)-1)){
			return cnt;
		}

		{
			SI a_, b_, c_;
			if( a_top < b_top ){ // B -> A 
				a_ = a + b_top;
				b_ = b - b_top;
				c_ = c;
			}else{ // A -> B 
				a_ = a - a_top;
				b_ = b + a_top;
				c_ = c;
			}
			State next = State(a_,P(b_,c_));
			if( !d.count(next) && cnt + 1 <= m ){
				d[next] = cnt + 1;
				q.push(next);
			}
		}
		{
			SI a_, b_, c_;
			if( c_top < b_top ){ // B -> C 
				a_ = a;
				b_ = b - b_top;
				c_ = c + b_top;
			}else{ // C -> B 
				a_ = a;
				b_ = b + c_top;
				c_ = c - c_top;
			}
			State next = State(a_,P(b_,c_));
			if( !d.count(next) && cnt + 1 <= m ){
				d[next] = cnt + 1;
				q.push(next);
			}
		}
	}
	return -1;
}

int main(){
	while( cin >> n >> m , n || m ){
		// 
		cup[0] = cup[1] = cup[2] = 0;

		for(int i=0 ; i < 3 ; i++ ){
			int k, e;
			cin >> k;
			for(int j=0 ; j < k ; j++ ){
				cin >> e;
				cup[i] |= (1 << (e-1));
			}
		}
		int ans = bfs();
		cout << ans << endl;
	}
}

