#include <iostream>
#include <algorithm>

using namespace std;

typedef long long lli;

const lli INF = 1000000000;

class S{
public:
  lli t[3], cost;
  S(){}
  S(lli a, lli b, lli c, lli cost) : cost(cost){
    t[0] = a;
    t[1] = b;
    t[2] = c;
  }
};

lli n, m, ans;

lli calc(S s, lli pres, lli pret){
  if(ans <= s.cost) return INF;
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  if(s.cost == m) return INF;
  lli res = INF;
  lli tmp[3];
  fill(tmp, tmp+3, -1);
  for(lli i=n-1;i>=0;i--){
    for(lli j=0;j<3;j++){
      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(lli i=0;i<3;i++){
    for(lli j=0;j<3;j++){
      if(abs(i-j) != 1) continue;
      if(i == pret && j == pres) continue;
      if(tmp[i] > tmp[j]){
        S t = s;
        t.t[i] &= ~(1 << tmp[i]);
        t.t[j] |= 1 << tmp[i];
        t.cost++;
        res = min(res, calc(t, i, j));
        ans = min(ans, res);
      }
    }
  }
  return res;
}

lli solve(S s){
  if(s.t[0] == (1 << n) - 1 || s.t[2] == (1 << n) - 1) return s.cost;
  lli res = INF;
  lli tmp[3];
  fill(tmp, tmp+3, -1);
  for(lli i=n-1;i>=0;i--){
    for(lli j=0;j<3;j++){
      if(s.t[j] & 1 << i) tmp[j] = max(tmp[j], i);
    }
  }
  for(lli i=0;i<3;i++){
    if(tmp[i] != -1){
      for(lli j=0;j<3;j++){
        if(abs(i-j) != 1) continue;
        if(tmp[i] > tmp[j]){
          S t = s;
          t.t[i] &= ~(1 << tmp[i]);
          t.t[j] |= 1 << tmp[i];
          t.cost++;
          res = min(res, calc(t, i, j));
          ans = min(ans, res);
        }
      }
    }
  }
  if(res == INF) return -1;
  return res;
}

main(){
  while(cin >> n >> m && (n|m)){
    lli data[3];
    for(lli i=0;i<3;i++){
      lli size;
      cin >> size;
      data[i] = 0;
      for(lli j=0;j<size;j++){
        lli in;
        cin >> in;
        data[i] |= 1 << (in-1);
      }
    }
    ans = INF;
    cout << solve(S(data[0], data[1], data[2], 0)) << endl;
  }
}

