#include<iostream>
#include<vector>
#include<queue>
#include<map>
using namespace std;

int n, m;

int bfs(vector<int>& cup){
	map<vector<int>,int> d;
	queue<vector<int> > q;
	d[cup] = 0;
	q.push(cup);
	while( !q.empty() ){
		vector<int> now = q.front(); q.pop();

		if( now[0] == ((1<<n)-1) || now[2] == ((1<<n)-1) ){
			return d[now];
		}
		for(int i=0 ; i < 2 ; i++ ){
			vector<int> next = now;
			int a = now[i];
			int b = now[i+1];
			for(int j = n ; j >= 0 ; j-- ){
				if( (a & (1<<j)) || (b & (1<<j)) ){
					if( a > b ){
						a -= (1<<j);
						b += (1<<j);
					}else{
						a += (1<<j);
						b -= (1<<j);
					}
					break;
				}
			}
			next[i] = a;
			next[i+1] = b;
			if( !d.count(next) && d[now] + 1 <= m ){
				d[next] = d[now] + 1;
				q.push(next);
			}
		}
	}
	return -1;
}

