#define AB 0
#define BA 1
#define AC 2
#define CA 3
#define BC 4
#define CB 5

inline int mostleft(int x){
  int ret = 0;
  if(x == 0) return -1;
  if(x & 0xff00){ ret += 8; x >>= 8; }
  if(x & 0x00f0){ ret += 4; x >>= 4; }
  if(x & 0x000C){ ret += 2; x >>= 2; }
  if(x & 0x0002){ ret += 1; x >>= 1; }
  return ret;
}

int n, m;
int solve(int a, int b, int c, int prev){
  int ret = 0;
  const int ok = (1 << n) - 1;

  while( ++ret < m ){
    //printf("ret=%d, prev=%d: %x %x %x\n",ret,prev,a,b,c);
    if(a == ok || c == ok) return ret;

    if(a < b && prev != AB){
      int g = 1<<mostleft(b);
      prev = BA;
      a ^= g;
      b ^= g;
    }else if(b < a && prev != BA){
      int g = 1<<mostleft(a);
      prev = AB;
      a ^= g;
      b ^= g;
    }else if(b < c && prev != BC){
      int g = 1<<mostleft(c);
      prev = CB;
      b ^= g;
      c ^= g;
    }else if(c < b && prev != CB){
      int g = 1<<mostleft(b);
      prev = BC;
      b ^= g;
      c ^= g;
    }
  }

  return m + 1;
}

