    if(b < a){
      int g = 1<<mostleft(a);
      ans = min(ans, solve(a ^ g, b ^ g, c, AB));
    }
    if(b < c){
      int g = 1<<mostleft(c);
      ans = min(ans, solve(a, b ^ g, c ^ g, CB));
    }
    if(c < b){
      int g = 1<<mostleft(b);
      ans = min(ans, solve(a, b ^ g, c ^ g, BC));
    }

    if(ans > m) puts("-1");
    else printf("%d\n",ans);

  }
  return 0;
}

