#define MAX 16
#define A 0
#define B 1
#define C 2

using namespace std;

template<typename _T>
_T &abs(const _T &x){if(x<0)return -x;else return x;}

int cost[3][MAX];
int main(){
	cost[1][1] = 1;
	cost[2][1] = 2;
	for(int i = 1; i < 3; ++i){
		for(int j = 2; j < MAX; ++j){
			if( i == 1 ){
				cost[i][j] = 3 * cost[i][j - 1] + 1;
			}else{
				cost[i][j] = 2 * cost[1][j];
			}
		}
	}

	while(true){
		int ans = 0;
		int places[MAX];
		int maxs[3] = {0,};
		int nums[3] = {0,};
		int cups[3][MAX];
		int n,m;

		scanf("%d%d", &n, &m);
		if( n == 0 && m == 0 )
			break;

		for(int i = 0; i < 3; ++i){
			int t;
			scanf("%d", &t);
			for(int j = 0; j < t; ++j){
				scanf("%d", &cups[i][j]);
				places[ cups[i][j] ] = i;
				nums[i]++;
				maxs[i] = max(maxs[i],cups[i][j]);
			}
		}

		if( nums[B] == 1 ){
			if( maxs[A] < maxs[B] ){
				places[ cups[B][0] ] = A;
				ans += 1;
			}else if( maxs[C] < maxs[B] ){
				places[ cups[B][0] ] = C;
				ans += 1;
			}
		}

		for(int i = n; i >= 2; --i){
			int d = abs( places[ i ] - places[i - 1] );
			ans += cost[d][n - i + 1];
			places[ i ] = places[ i - 1 ];
		}

		if( places[ 1 ] == B ){
			ans += cost[1][n];
		}

		printf("%d\n", ans <= m ? ans : -1);
	}
	return 0;
}

