using namespace std;
int dfs(int from, int to, int cnt, int a[], int b[], int c[], int n, int m) {
	int i;
	if (from==0) {
		for (i=1; i<n; i++) if (a[i]==0) break;
		int an=a[i-1]; a[i-1]=0;
		for (i=0; i<n; i++) if (b[i]==0) break;
		if (i==0) b[0]=an;
		else if (b[i-1]>an) return -1;
		else b[i]=an;
	} else if (from==2) {
		for (i=1; i<n; i++) if (c[i]==0) break;
		int cn=c[i-1]; c[i-1]=0;
		for (i=0; i<n; i++) if (b[i]==0) break;
		if (i==0) b[0]=cn;
		else if (b[i-1]>cn) return -1;
		else b[i]=cn;
	}
	if ((a[0]==0&&b[0]==0)||(b[0]==0&&c[0]==0)) return cnt;
	else if (i==m) return -1;
	int res=m+1;
	for (int i=0; i<3; i++) {
		if (i==1) {
			if (b[0]==0) continue;
			int res1=dfs(i,0,cnt+1,a,b,c,n,m);
			int res2=dfs(i,2,cnt+1,a,b,c,n,m);
			if (res1<res2&&res1!=-1) res=min(res,res1);
			else if (res1>=res2&&res2!=-1) res=min(res,res2);
		} else {
			if (i==0&&a[0]==0) continue;
			if (i==2&&c[0]==0) continue;
			int temp=dfs(i,1,cnt+1,a,b,c,n,m);
			if (temp!=-1) res=min(res,temp);
		}
	}
	if (res==m+1) return -1;
	else return res;
}
