using namespace std;

int n, m, cnt;
int pos[20], tmpPos[20];

void copyPos(int *fromPos, int *toPos){
  for(int i = 0; i < 20; i++){
    toPos[i] = fromPos[i];
  }
}

void toC(int x);

void toA(int x){
  if(pos[x] == 0){
    return;
  }

  if(pos[x] == 2){
    //xA
    for(int i = x + 1; i < n; i++){
      toA(i);
      if(cnt > m) return;
    }

    //Bx
    cnt++;
    if(cnt > m) return;

    pos[x] = 1;
  }

  if(pos[x] == 1){
    //xC
    for(int i = x + 1; i < n; i++){
      toC(i);
      if(cnt > m) return;
    }

    //Ax
    cnt++;
    if(cnt > m) return;

    pos[x] = 0;
  }
}

void toC(int x){
  if(pos[x] == 2){
    return;
  }

  if(pos[x] == 0){
    //xC
    for(int i = x + 1; i < n; i++){
      toC(i);
      if(cnt > m) return;
    }

    //Bx
    cnt++;
    if(cnt > m) return;

    pos[x] = 1;
  }

  if(pos[x] == 1){
    //xA
    for(int i = x + 1; i < n; i++){
      toA(i);
      if(cnt > m) return;
    }

    //Cx
    cnt++;
    if(cnt > m) return;

    pos[x] = 2;
  }
}

void solve(){
  int res = m + 1;

  copyPos(pos, tmpPos);
  cnt = 0;

  for(int i = 0; i < n; i++){
    toA(i);
    if(cnt > m) break;
  }

  res = min(res, cnt);
  copyPos(tmpPos, pos);
  cnt = 0;

  for(int i = 0; i < n; i++){
    toC(i);
    if(cnt > m) break;
  }

  res = min(res, cnt);

  cout << (res == m + 1 ? -1 : res) << endl;
}

int main(){
  while(cin >> n >> m, n || m){
    for(int i = 0; i < 3; i++){
      int a;
      cin >> a;

      for(int j = 0; j < a; j++){
        int x;
        cin >> x;
        x--;
        pos[x] = i;
      }
    }

    solve();
  }
}

