};
bool operator==(const puzzle&left, const puzzle&right) {
	rep(i, 3) {
		if (strcmp(left.f[i], right.f[i]))return false;
	}
	return true;
}
template<>
class hash<puzzle> {
public:
	inline size_t operator()(const puzzle&p) const {
		size_t cnt = 0;
		rep(i, 3) {
			hash<string>H;
			cnt += (i + 1)*H(p.f[i]);
		}
		return cnt;
	}
};

