#include <bits/stdc++.h>
using namespace std;

static const int MAX_N = 15;
static const int MAX_M = 15000000;

enum Tray { A, B, C, NUM };
enum Dir { LEFT = -1, RIGHT = 1 };

static int dir[]{LEFT, RIGHT};

struct Data {
  set<int, greater<int>> tray[NUM];
  Tray from, to;
  int count;
};

int N, M;

int bfs(Data d) {
  queue<Data> que;
  que.push(d);

  while (!que.empty()) {
    Data d = que.front(); que.pop();
    if (d.tray[A].size() == N || d.tray[C].size() == N)
      return d.count;
    if (d.count >= M)
      break;
    for (int t{A}; t <= C; ++t) {
      for (int i{}; i < 2; ++i) {
        int nt = t + dir[i];
        if (nt < A || nt > C)
          continue;
        if (t == d.to && nt == d.from)
          continue;
        auto it = d.tray[t].begin();
        if (it == d.tray[t].end())
          continue;
        int t_cup = *it;
        it = d.tray[nt].begin();
        int nt_cup{};
        if (it != d.tray[nt].end()) {
          nt_cup = *it;
        }
        if (t_cup < nt_cup)
          continue;
        Data nd = d;
        nd.tray[nt].insert(t_cup);
        nd.tray[t].erase(t_cup);
        nd.from = static_cast<Tray>(t);
        nd.to = static_cast<Tray>(nt);
        ++nd.count;
        que.push(nd);
      }
    }
  }

  return -1;
}

int main() {
  while (true) {
    cin >> N >> M;
    if (N == 0 && M == 0)
      break;
    Data d{};
    for (int t{A}; t <= C; ++t) {
      int num;
      cin >> num;
      for (int i{}; i < num; ++i) {
        int cup;
        cin >> cup;
        d.tray[t].insert(cup);
      }
    }
    cout << bfs(d) << endl;
  }
  return 0;
}

