	else{
// cout << "------------" << f << " ----------" << endl;
// for(int i=0;i<3;i++){
// 	for(int j=0;j<d[i].size();j++){
// 		cout << d[i][j] << " ";
// 	}cout << endl;
// }
// cout << "------------------------------" << endl;
		if(d[0].size()==n || d[n-1].size()==n)return f;
		int res = m+1;
		for(int i=0;i<d.size()-1;i++){
			if(d[i].empty())continue;
			if(d[i+1].empty()||d[i].back()>d[i+1].back()){
				d[i+1].push_back(d[i].back());
				d[i].pop_back();
				res = min(res,dfs(d,f+1));
				d[i].push_back(d[i+1].back());
				d[i+1].pop_back();
			}
		}
		for(int i=d.size()-1;i>0;i--){
			if(d[i].empty())continue;
			if(d[i-1].empty()||d[i].back()>d[i-1].back()){
				d[i-1].push_back(d[i].back());
				d[i].pop_back();
				res = min(res,dfs(d,f+1));
				d[i].push_back(d[i-1].back());
				d[i-1].pop_back();
			}
		}
		return res;
	}
}

