int cup[3];
int cup_move[4][2] = {{0,1},{1,0},{1,2},{2,1}};
int stack[MMAX][3];

int move(int temp[3],int a,int amin, int amax)
{
	int i,l,m;
	bool flag;

	for(m=0;m<4;m++){
		if(temp[cup_move[m][0]]>temp[cup_move[m][1]]){
			l= 1<<NMAX;
			while((l & temp[cup_move[m][0]]) == 0) l>>=1;
			temp[cup_move[m][0]] -= l;
			temp[cup_move[m][1]] += l;
			a++;
			stack[a][0]=temp[0];
			stack[a][1]=temp[1];
			stack[a][2]=temp[2];

			if(a>amax){
				return a--;
			}
			if(temp[1]==0 && (temp[0]==0 || temp[2]==0)){
				if(amin>a) amin=a;
				return a--;
			}

			flag = false;
			for(i=0;i<a;i++){
				if(stack[i][0]==temp[0] && stack[i][1]==temp[1] && stack[i][2]==temp[2]){
					a--;
					temp[0]=stack[a][0];
					temp[1]=stack[a][1];
					temp[2]=stack[a][2];
					flag = true;
					break;
				}
			}
			if(!flag) amin = move(temp,a,amin,amax);
		}
	}
	return amin;
}

