#define NMAX 15
#define MMAX 15000000

const int cup_move[4][2] = {{0,1},{1,0},{1,2},{2,1}};
int stack[MMAX][3];

int move(int temp[3],int a,int amin, int amax)
{
	int i,l,m;
	int flag;

	for(m=0;m<4;m++){
		if(temp[cup_move[m][0]]>temp[cup_move[m][1]]){
			l = 1<<NMAX;
			while((l & temp[cup_move[m][0]]) == 0) l>>=1;

			temp[cup_move[m][0]] -= l;
			temp[cup_move[m][1]] += l;

			flag = 0;
			for(i=a;i>=0;i--){
				if(stack[i][0]==temp[0] && stack[i][1]==temp[1] && stack[i][2]==temp[2]){
					temp[cup_move[m][0]] += l;
					temp[cup_move[m][1]] -= l;
					flag=1;
					break;
				}
			}
			if(flag) continue;

			a++;
			stack[a][0]=temp[0];
			stack[a][1]=temp[1];
			stack[a][2]=temp[2];

			if(a>amax){
				return a--;
			}

			if(temp[1]==0 && (temp[0]==0 || temp[2]==0)){
				if(amin>a) amin=a;
				return a--;
			}

			amin = move(temp,a,amin,amax);
		}
	}
	return amin;
}

