typedef struct {
    int time;
    int tower[3][15];
    int t[3];
} HANOI;

HANOI queue[50000];
int head, tail;
int pow[30];

void enq(HANOI t)
{
	queue[tail % 50000] = t;
	tail++;
}

void deq(HANOI *t)
{
	*t = queue[head % 50000];
	head++;
}

char v[15000000];

int move(HANOI temp, int from, int to, int pw)
{
    int num;
    int i, j;
    HANOI first;
    if ((temp.t[from] > 0 && temp.t[to] > 0 && temp.tower[from][temp.t[from] - 1] > temp.tower[to][temp.t[to] - 1]) ||temp.t[from] > 0 && temp.t[to] == 0){
        first = temp;
        first.tower[to][temp.t[to]] = first.tower[from][temp.t[from] - 1];
        first.tower[from][temp.t[from] - 1] = 0;
        first.t[from]--;
        first.t[to]++;
        first.time++;
        num = 0;
        for (i = 0; i < 3; i++){
            for (j = 0; j < first.t[i]; j++){
                num += i * pow[first.tower[i][j] - 1];
            }
        }
        if (v[0] == 1 || v[pw] == 1){
            printf("%d\n", first.time);
            return (1);
        }
        if (!v[num]){
            v[num] = 1;
            enq(first);
        }
    }
    return (0);
}

