			}
		}

		p = 0;
		cnt = 0;
		for(i = n - 1; i >= 0; --i){
			d = abs(p - c[i]);
			cnt += pow3[i] * d;
			if(d == 1){
				p = 2 - p;
			}
		}

		k = pow3[n] - 1 - cnt;
