#include <stdio.h>
#define min(a, b) ((a) < (b) ? (a) : (b))

int right_start(int A, int B, int C);

int left_start(int A, int B, int C)
{
	if (!B && !C) return 0;

	if (A & 1){
		return left_start(A>>1, B>>1, C>>1);
	}
	if (B & 1){
		return right_start(A>>1, B>>1, C>>1 ) + 1 + left_start(0, 0, (A|B|C)>>1 );
	}
	if (C & 1){
		return left_start(A>>1,B>>1,C>>1) + 1 + right_start((A|B|C)>>1, 0, 0) + 1 + left_start(0, 0, (A|B|C)>>1);
	}
}

int right_start(int A, int B, int C)
{
	if (!A && !B) return 0;

	if (C & 1){
		return right_start(A>>1, B>>1, C>>1);
	}
	if (B & 1){
		return left_start(A>>1, B>>1, C>>1) + 1 + right_start((A|B|C)>>1, 0, 0);
	}
	if (A & 1 ){
		return right_start(A>>1, B>>1, C>>1) + 1 + left_start(0, 0, (A|B|C)>>1) + 1 + right_start((A|B|C)>>1, 0, 0);
	}
}

