#include<stdio.h>

int cup[15],n;

long beki(int a,int b){
	int i;
	long ans=1;
	for(i=0;i<b;i++){
		ans*=a;
	}
	return ans;
}

long mini(int min,int c){
	if(min==(n-1)){
		switch(cup[min]-c){
			case 0:
			return 0;
			case 1:
			case -1:
			return 1;
			case 2:
			case -2:
			return 2;
		}
	}else{
		switch(cup[min]){
			case 0:
			if(c==0){
				return mini(min+1,0);
			}else if(c==1){
				return mini(min+1,2)+1+(beki(3,n-min-1)-1/2);
			}else{
				return mini(min+1,2)+(2*beki(3,n-min-1));
			}
			case 1:
			if(c==0){
				return mini(min+1,2)+beki(3,n-min-1);
			}else if(c==1){
				return mini(min+1,1);
			}else{
				return mini(min+1,0)+beki(3,n-min-1);
			}
			case 2:
			if(c==0){
				return mini(min+1,2)+(2*beki(3,n-min-1));
			}else if(c==1){
				return mini(min+1,0)+1+((beki(3,n-min-1)-1)/2);
			}else{
				return mini(min+1,2);
			}
		}
	}
}

