    HANOI start, temp;
    int i, j;

    while (1){
        scanf("%d%d", &n, &m);

        if (n + m == 0){
            break;
        }
        memset(start.tower, 0, sizeof(start.tower));
        for (i = 0; i < 3; i++){
            scanf("%d", &start.t[i]);
            for (j = 0; j < start.t[i]; j++){
                scanf("%d", &start.tower[i][j]);
            }
        }

        memset(v, 0, sizeof(v));

        start.time = 0;
        head = tail = 0;
        enq(start);

        while (head != tail){
            deq(&temp);

            if (order(0, temp.tower, n) || order(2, temp.tower, n)){
                printf("%d\n", temp.time);
                break;
            }

            if (temp.time > m){
                printf("-1\n");
                break;
            }

            move(temp, 0, 1);
            move(temp, 1, 0);
            move(temp, 1, 2);
            move(temp, 2, 1);
        }
    }

    return (0);
}

#include <stdio.h>

#define NMAX 15
#define MMAX 15000000

const int cup_move[4][2] = {{0,1},{1,0},{1,2},{2,1}};
int stack[MMAX][3];

int move(int temp[3],int a,int amin, int amax)
{
	int i,l,m;
	int flag;

	for(m=0;m<4;m++){
		if(temp[cup_move[m][0]]>temp[cup_move[m][1]]){
			l= 1<<NMAX;
			while((l & temp[cup_move[m][0]]) == 0) l>>=1;
			temp[cup_move[m][0]] -= l;
			temp[cup_move[m][1]] += l;
			a++;
			stack[a][0]=temp[0];
			stack[a][1]=temp[1];
			stack[a][2]=temp[2];

			if(a>amax){
				return a--;
			}
			if(temp[1]==0 && (temp[0]==0 || temp[2]==0)){
				if(amin>a) amin=a;
				return a--;
			}

			flag = 0;
			for(i=a-1;i>=0;i--){
				if(stack[i][0]==temp[0] && stack[i][1]==temp[1] && stack[i][2]==temp[2]){
					a--;
					temp[0]=stack[a][0];
					temp[1]=stack[a][1];
					temp[2]=stack[a][2];
					flag = 1;
					break;
				}
			}
			if(!flag) amin = move(temp,a,amin,amax);
		}
	}
	return amin;
}

