#include <stdio.h>
#define N_MAX 15
#define M_MAX 15000000

int memo[N_MAX][3][3],ismemoized[N_MAX][3][3];

int main(){
  int card[15],n,m,i,j,from,to,rest,cnt,ans;

  while(1){
    scanf("%d %d",&n,&m);
    ans = m+1;

    if(n==0&&m==0) return 0;

    for(i=0; i<3; i++){
      int t1,t2;
      scanf("%d",&t1);
      for(j=0; j<t1; j++){
	scanf("%d",&t2);
	card[t2-1] = i;
      }
    }

    for(i=0; i<=2; i+=2){
      from = i; cnt = 0;
      for(j=1; j<=n; j++){
	to = card[j-1];
	rest = 3-(from+to);

	switch(abs(from-to)){
	case 1:
	  cnt += move(n-j,from,rest) + 1;
	  from = rest;
	  break;
	case 2:
	  cnt += move(n-j,from,to) + move(n-j,to,from) + 2;
	  break;
	}
      }
      ans = (ans>cnt)?cnt:ans;
    }


  }
}

int move(num,from,to){
  int rest = 3-(from+to),ans;

  if(ismemoized[num][from][to])
    return memo[num][from][to];

  if(num==0)
    ans = 0;
  else if(num==1)
    ans = abs(from-to);
  else
    switch(abs(from-to)){
    case 0:
      ans = 0;
      break;
    case 1:
      ans = move(num-1,from,rest)+move(num-1,rest,to)+1;
      break;
    case 2:
      ans = move(num,from,rest)+move(num,rest,to);
      break;
    }

  ans = (ans<=M_MAX)?ans:(M_MAX+1);

  ismemoized[num][from][to] = 1;
  memo[num][from][to] = ans;
  return ans;
}

