			queue[queue_top].privious[i].num = Hanoi[i].num;
			for(j=1;j<=Hanoi[i].num;j++){
				queue[queue_top].current[i].cups[j] = Hanoi[i].cups[j];
				queue[queue_top].privious[i].cups[j] = Hanoi[i].cups[j];
			}
		}
		HanoiMove(Hanoi,Prev,0);
		if(min>m) printf("-1\n");
		else printf("%d\n",min);
	}

	return 0;
}

