int move(int card,int place)
{
  int cnt=0,ctray,diff,i,j;

  for(i=0; i<3; i++)
    for(j=0; j<num[place][i]; j++)
      if(card==tray[place][i][j]){
	ctray = i;
	goto out;
      }

 out:

  if(ctray==place)
    return 0;

  diff = (ctray<place)?1:-1;
  for(i=ctray; i!=place; i+=diff)
    cnt += move2(card,i,i+diff,place);

  return cnt;
}

int move2(int card,int from,int to,int goal)
{
  int cnt=0,rest=3-from-to;

  while(1){
    if(tray[goal][from][num[goal][from]-1] > card){
      cnt += move2(tray[goal][from][num[goal][from]-1],from,abs(rest-from)==1?rest:to,goal);
      continue;
    }

    if(tray[goal][to][num[goal][to]-1] > card){
      cnt += move2(tray[goal][to][num[goal][to]-1],to,abs(rest-to)==1?rest:from,goal);
      continue;
    }
    break;
  }

  num[goal][from]--;
  tray[goal][to][num[goal][to]++] = card;

  return cnt+1;
}

