      break;
    case 2:
      ans = move(num,from,rest)+move(num,rest,to);
      break;
    }

  ans = (ans<M_MAX)?ans:(M_MAX+1);

  ismemoized[num][from][to] = 1;
  memo[num][from][to] = ans;
