		}


//dbg for(i=0;i<n;i++)printf("%d ",POS[i]);puts("");

		SerialNo=DECODE_3Gray2int(n,POS);	//sol. determined from 3-adic/n-bit GrayCode

		max=1;
		for(i=0;i<n;i++)max*=3;
		max=max-1;

